import React, { Component } from "react";
import LayoutContentWrapper from "../../components/utility/layoutWrapper.js";
import LayoutContent from "../../components/utility/layoutContent";
import Form from '../../../src/components/uielements/form';
import Input from '../../../src/components/uielements/input';
import PageHeader from '../../../src/components/utility/pageHeader';
import Box from '../../../src/components/utility/box';
import LayoutWrapper from '../../../src/components/utility/layoutWrapper.js';
import IntlMessages from '../../../src/components/utility/intlMessages';
import FormValidation from './FormValidation';
import { VCardWidgetWrapper } from './style';
import TimeSelectForm from "./TimeSelect/TimeSelect";


const FormItem = Form.Item;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 5 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 12 }
  }
};


export default class extends Component {
  render() {
    const { style } = this.props;
    return (
      <Box>
        <FormValidation />
        <VCardWidgetWrapper className="isoVCardWidgetWrapper" style={style}>
          <div className="isoVCardImage">
            <img src="https://img.favpng.com/14/23/22/computer-icons-share-icon-landscape-png-favpng-U96r3erAyp2ZyUerjJJWk6QAG.jpg" alt="placeholder" />
          </div>

          <div className="isoVCardBody">
            <h3 className="isoName">Hi, {<IntlMessages id="booking.name" />} </h3>
            <span className="isoDesgTitle">Thank you for your booking with Bukit Vista. Here are the details of your current booking:</span>

            <p className="isoDescription">Property name: <span className="isoInfo"> Villa Bagus by Bukit Vista </span></p>
            <p className="isoDescription">Check in date: <span className="isoInfo">3 April 2019 </span> </p>
            <p className="isoDescription">Check out date: <span className="isoInfo">5 April 2019 </span> </p>
            <p className="isoDescription"> Arrival time: <span className="isoInfo"> (please set your arrival time)</span></p>

          </div>
          <div className="App">
            <TimeSelectForm />
          </div>
        </VCardWidgetWrapper>
      </Box>



    );
  }
}
