import React, { Component } from "react";
import LayoutContentWrapper from "../../components/utility/layoutWrapper.js";
import LayoutContent from "../../components/utility/layoutContent";
import Form from '../../../src/components/uielements/form';
import Input from '../../../src/components/uielements/input';
import PageHeader from '../../../src/components/utility/pageHeader';
import Box from '../../../src/components/utility/box';
import LayoutWrapper from '../../../src/components/utility/layoutWrapper.js';
import IntlMessages from '../../../src/components/utility/intlMessages';
import FormValidation from './FormValidation';
import Cards from '../../../src/components/uielements/card';
import styled from 'styled-components';
import { palette } from 'styled-theme';


const FormItem = Form.Item;

const formItemLayout = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 5 }
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 12 }
  }
};
const CardComp = props => <Cards {...props} />;

const Card = styled(CardComp)`
  .ant-card-head {
    background-color: ${palette('grayscale', 4)};
    ${'' /* border-bottom: 1px solid ${palette('border', 0)}; */} border-bottom: 0;

    .ant-card-head-title {
      color: ${palette('text', 0)};
    }
  }

  .ant-card-extra {
    a {
      color: ${palette('primary', 0)};
      text-decoration: none;

      &:focus {
        text-decoration: none;
      }
    }
  }

  .ant-card-body {
    p {
      font-size: 13px;
      color: ${palette('text', 3)};
      line-height: 1.5;
      margin-bottom: 14px;

      &:last-child {
        margin-bottom: 0;
      }
    }
  }

  &.ant-card-bordered {
    border: 1px solid ${palette('border', 0)};

    .ant-card-head {
      border-bottom: 1px solid ${palette('border', 0)};
    }

    &:hover {
      border: 1px solid ${palette('border', 0)} !important;
    }
  }

  &.ant-card-loading {
    .ant-card-body {
      p {
        margin-bottom: 0;
      }
    }
  }

  .custom-card {
    padding: 10px 16px;
    h3 {
      color: ${palette('text', 1)};
      font-weight: 500;
    }
    p {
      color: ${palette('grayscale', 0)};
    }
  }

  .custom-image img {
    display: block;
  }
`;

export default class extends Component {
  render() {
    return (
      <LayoutWrapper>
      <PageHeader>Form Submit Validation</PageHeader>
      <Box>
        <FormValidation />
      </Box>
      <Card
                  title={<IntlMessages id="uiElements.cards.cardTitle" />}
                  bordered={false}
                  style={{ width: '100%' }}
                >
                  <p>{<IntlMessages id="uiElements.cards.lorem" />}</p>
                  <p>{<IntlMessages id="uiElements.cards.cardContent" />}</p>
                </Card>
    </LayoutWrapper>
    );
  }
}
